#Combien de handle present dans l'array@arr_journalist
def combien_de_journalistes
  system('clear')
  result_combien = @arr_journalist.select {|journalist| journalist.include? ("@")}
  puts "Calcule du nombre de journalistes présent :"
  puts "Il y a #{result_combien.length} journalistes présent"
end
#Ordre alphabetique de l'array@arr_journalist
def ordre_alphabetique
  system('clear')
  alpha_array_sort = @arr_journalist.sort{|a,z| a <=> z}
  puts "Voici la liste  des journaliste en ordre alphabetique"
  puts " "
  puts alpha_array_sort
end
#Handle le plus court present dans l'array@arr_journalist
def le_plus_court_de_la_liste
  system('clear')
  result_court = @arr_journalist.sort_by(&:length)[0]
  puts "Voici le journaliste avec le handle le plus court de la liste"
  puts " "
  puts result_court

end
#Combien de handle avec 5 caractères dans l'array@arr_journalist
def cb_avec_cinq_car
  system('clear')
  result_cinq = @arr_journalist.count {|cinq| cinq.length == 6}
  puts result_cinq

end
#Combien de handle commencant par une Maj present dans l'array@arr_journalist
def cb_par_une_majuscule
  system('clear')
  result_maj = @arr_journalist.count {|maj| maj[1] == maj[1].upcase}
  puts result_maj

end
#Classement par taille de l'array@arr_journalist
def par_taille
  system('clear')
  result_taille = @arr_journalist.sort_by(&:length)
  puts "Voici la liste par ordre croissant des longueurs des handles"
  puts " "
  puts result_taille

end
#Ou est E-penser dans l'array@arr_journalist
def ou_e_penser
  system('clear')
  result_epenser = @arr_journalist.find_index("@epenser")
  puts "Recherche du journaliste : e-penser"
  sleep(2)
  puts "Le journaliste e-penser se trouve à la #{result_epenser}eme place"

end

#Repartition par taille de chaine de caractère de l'array@arr_journalist
def handle_taille
  result_handle = {}
  counter = 0
  @arr_journalist.each do |handle|
    counter = handle.length
    if result_handle[counter].nil?
        result_handle[counter] = 1
      else
        result_handle[counter] = result_handle[counter] + 1
      end
    end
    result_handle.each{|caractere, value| puts "Il y a #{value} de handles qui ont #{caractere} caractères"}

end
############################## PARTIE MENU ####################################################################
def recap
  result_taille = @arr_journalist.sort_by(&:length)
  result_epenser = @arr_journalist.find_index("@epenser")
  result_maj = @arr_journalist.count {|maj| maj[1] == maj[1].upcase}
  result_cinq = @arr_journalist.count {|cinq| cinq.length == 6}
  result_court = @arr_journalist.sort_by(&:length)[0]
  alpha_array_sort = @arr_journalist.sort{|a,z| a <=> z}
  result_combien = @arr_journalist.select {|journalist| journalist.include? ("@")}
  system('clear')
  puts " Il y a #{result_combien.length} journalistes"
  puts " "
  sleep(2)
  puts " Le handle le plus court est : #{result_court}"
  puts " "
  sleep(2)
  puts " Il y a #{result_cinq} handles avec 5 caractères"
  puts " "
  sleep(2)
  puts " Il y a #{result_maj} handles commençant par une majuscule"
  puts " "
  sleep(2)
  puts " Voici la liste en ordre alphabetique :"
  puts " "
  puts alpha_array_sort
  puts " "
  puts " "
  sleep(5)
  puts " Voici la liste en ordre de grandeur :"
  puts " "
  puts result_taille
  puts " "
  puts " "
  sleep(5)
  puts " Voici la place de E-penser : #{result_epenser}"
  puts " "
  sleep(3)
  puts " Voici la répartition des handles :"
  puts " "
  handle_taille()
  sleep(60)
end

def perfom
  puts " ====   Bienvenue dans DataInDeep by D1sh   ==== "
  puts " |===============================================|"
  puts "       (selection de la cible par numéro)"
  puts " |===============================================|"
  puts " "
  puts "     Que souhaitez-vous appliquer à ce tableau ?"
  puts " "

  puts " |===============================================|"
  puts "      [1] Combien de handle ?"
  puts " |===============================================|"
  puts "      [2] Handle(s) le plus court ?"
  puts " |===============================================|"
  puts "      [3] Handle contenant 5 caractères ?"
  puts " |===============================================|"
  puts "      [4] Handle commencant par une maj ?"
  puts " |===============================================|"
  puts "      [5] Handle en ordre alphabetique ?"
  puts " |===============================================|"
  puts "      [6] Handle en ordre suivant la longueur ?"
  puts " |===============================================|"
  puts "      [7] Position du journaliste E-penser ?"
  puts " |===============================================|"
  puts "      [8] Répartition des handles par taille ?"
  puts " |===============================================|"
  puts "      [9] Récapitulatif ?"
  puts " |===============================================|"

  user_choice = gets.chomp.to_i
  if user_choice == 1
    combien_de_journalistes()
    sleep (3)
    system('clear')
    perfom()

  end
  if user_choice == 2
    le_plus_court_de_la_liste()
    sleep (5)
    system('clear')
    perfom()

  end
  if user_choice == 3
    cb_avec_cinq_car()
    sleep (5)
    system('clear')
    perfom()

  end
  if user_choice == 4
    cb_par_une_majuscule()
    sleep (5)
    system('clear')
    perfom()

  end
  if user_choice == 5
    ordre_alphabetique()
    sleep (15)
    system('clear')
    perfom()

  end
  if user_choice == 6
    par_taille()
    sleep (15)
    system('clear')
    perfom()

  end
  if user_choice == 7
    ou_e_penser()
    sleep (5)
    system('clear')
    perfom()

  end
  if user_choice == 8
    handle_taille()
    sleep (5)
    system('clear')
    perfom()

  end
  if user_choice == 9
    recap()
    perfom()
  end


end

perfom()
